import { BsFolderFill } from "react-icons/bs";
import { AiFillCloseCircle } from "react-icons/ai";
import Skills from "./Skills";
import { useState } from "react";

function App() {
  const [skills, showSkill] = useState(true)
  return (
    <div className="App">
      <main className='bg-black min-h-screen'>
      <section  x-data="{modalOpen: false}" className=' bg-red-300 w-full h-1/2 flex items-center justify-center text-white'>
        <div className=' my-40 justify-center items-center'>
          <h1 className=' text-4xl border p-10 rounded-full shadow-xl hover:shadow-white hover:shadow-inner hover:cursor-pointer  text-slate-900'> <b className='underline'>UJWAL</b> KUMAR MAHATO</h1>
        </div>
      </section>
        <div className='bg-red-300 pb-20 text-center text-2xl'> {"<"} Developer {"/ >"}</div>
      <section className='flex justify-center h-auto text-white'>

          <a className='p-14 hover:text-2xl hover:cursor-pointer'><span className='justify-center text-4xl'><BsFolderFill /></span>SKILLS</a>

          <a className='p-14 hover:text-2xl hover:cursor-pointer'><span className='text-4xl'><BsFolderFill /></span>PROJECTS</a>

          <a className='p-14 hover:text-2xl hover:cursor-pointer'><span className='text-4xl'><BsFolderFill /></span>ABOUT</a>
      </section>
      <div id="DIV_01" className='absolute flex text-white bg-black justify-center top-0 left-0 w-full h-full bg-opacity-80'>
        <div className='bg-slate-900 bg-opacity-70 overflow-auto scrollbar-hide text-white my-40 h-auto w-96'>
          {skills?`<h3 className="text-center text-2xl">Skills</h3><hr />
          {<Skills  />}`:''}
        </div>
        <div className="absolute text-4xl bottom-10 hover:text-6xl hover:cursor-pointer">
          <AiFillCloseCircle />
        </div>
      </div>
    </main>
    </div>
  );
}

export default App;
