import React from 'react'
import Projects from "./Projects";
import About from "./About";
import Skills from "./Skills";
import { useState } from 'react';

export default function Display({show}) {
    const [skills, showSkill] = useState(false)
    const [projects, showprojects] = useState(false)
    const [about, setAbout] = useState(false)
    console.log(show)
    const show_switch=()=>{
        switch(show){
            case "skills": const [skills, showSkill] = useState(true); break;
            case "projects": const [projects, showprojects] = useState(true); break;
            case "about": const [about, setAbout] = useState(true); break;
        }
    }
    return (
    <>
      <div className='absolute flex text-white bg-black justify-center top-0 left-0 w-full h-full bg-opacity-80'>
        <div onLoad={show_switch} className='bg-slate-900 bg-opacity-70 overflow-auto scrollbar-hide text-white my-40 h-auto w-96'>
          {/* {show_switch} */}
          {skills?<Skills  />:''}
          {projects?<Projects  />:''}
          {about?<About  />:  ''}
        </div>
      </div>
    </>
  )
}
