import React from 'react'
import Projects from "./Projects";
import About from "./About";
import Skills from "./Skills";
import { useState } from 'react';

export default function Display({show}) {
    var [skills, showSkill] = useState(false)
    var [projects, showprojects] = useState(false)
    var [about, setAbout] = useState(false)
    console.log(show)
    const show_switch=(show)=>{
        switch(show){
            case "skills": showSkill(true); break;
            case "projects": showprojects(true); break;
            case "about": setAbout(true); break;
        }
    }
    return (
    <>
      <div className='absolute flex text-white bg-black justify-center top-0 left-0 w-full h-full bg-opacity-80'>
        <div className='bg-slate-900 bg-opacity-70 overflow-auto scrollbar-hide text-white my-40 h-auto w-96'>
          {show_switch}
          {skills?<Skills  />:''}
          {projects?<Projects  />:''}
          {about?<About  />:  ''}
        </div>
      </div>
    </>
  )
}
